INSERT INTO `resturant` (`rid`, `userid`, `area_id`, `chinise_id`, `rname`, `street`, `city`, `zipcode`, `country_id`, `contact`, `contact2`, `contact3`, `contact4`, `web`, `logo`, `rdesc`, `profile`, `keytitle`, `aactive`, `rating`, `restaurant_image`, `image1`, `image2`, `image3`, `image4`, `image5`, `image6`, `image7`, `image8`, `building`, `address2`, `county`) VALUES (33, 18, 10, 7, 'Moksh Direct', 'Unit 4, Waterside Business Park', 'Cardiff', 'CF3 2EQ', 'United Kingdom', '(029) 20361 321', '(029) 20 616 515', '', '', 'www.moksh.co.uk/mokshdirect/', 'companylogo/small_1241686555_ad_19.jpg', '', '\r\n<p>\r\n  <table cellspacing="2" cellpadding="2" width="100%" border="0">\r\n    <tbody>\r\n      <tr>\r\n        <td colspan="2" class="myriad15"><strong><span style="COLOR: #000000">Food for thought</span></strong></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">One of the very nicest things about life is the way we must regularly stop whatever it is we aredoing and devote our attention to eating.</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td class="myriad15_gray">\r\n          <div align="right"><span style="COLOR: #000000">-Luciano Pavaroti </span>\r\n          </div></td>\r\n        <td class="arial12"><span style="COLOR: #000000">&nbsp;</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="myriad15"><strong><span style="COLOR: #000000">Chef Stephen comments: </span></strong></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">On a culinary level we have made our very sincere efforts to keep the authenticity of the food true to its \r\n            <br />original recipes.We have used the best herbs and spices to give a natural aroma of Indian food.</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="tb_h">&nbsp;</td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="myriad15"><strong><span style="COLOR: #000000">Our Achievements</span></strong></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">Cardiff City Council Food Hygiene Awards 2007/08 \r\n            <br />Stephen Gomes - Chef of the Year 2006/07 \r\n            <br />Craft Guild of Chefs 2006/07 \r\n            <br />Master Chef Roll of Honour 2003</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="tb_h">&nbsp;</td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="myriad15"><strong><span style="COLOR: #000000">The Famous Food Critics Verdict</span></strong></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">We are already discussing a return visit before we have finished the obligatory naan mop-up. </span></td>\r\n      </tr>\r\n      <tr>\r\n        <td class="arial12">\r\n          <div align="right"><span style="COLOR: #000000"><strong>- Red Handed</strong></span>\r\n          </div></td>\r\n        <td class="myriad15_gray"><span style="COLOR: #000000">&nbsp;</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">Moksh has aspirations that go beyond a traditional curry house.</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td class="arial12">\r\n          <div align="right"><span style="COLOR: #000000"><strong>- Metro</strong></span>\r\n          </div></td>\r\n        <td class="myriad15_gray"><span style="COLOR: #000000">&nbsp;</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">1 am one of the few people in the country who doesnt like Indian food (there Ive said it!) but \r\n            <br />a visit to Moksh has set me on the road to conversion. </span></td>\r\n      </tr>\r\n      <tr>\r\n        <td class="arial12">\r\n          <div align="right"><span style="COLOR: #000000"><strong>- Cathy Owen / Echo</strong></span>\r\n          </div></td>\r\n        <td class="myriad15_gray"><span style="COLOR: #000000">&nbsp;</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">A visit to Moksh is throughly enjoyable and satisfying experience.</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td class="arial12">\r\n          <div align="right"><span style="COLOR: #000000"><strong>- Antonio Levay</strong></span>\r\n          </div></td>\r\n        <td class="myriad15_gray"><span style="COLOR: #000000">&nbsp;</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">Moksh aims to cater for those who love their curry, but also love chic and stylish surroundings.</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td class="arial12">\r\n          <div align="right"><span style="COLOR: #000000"><strong>- Ladies First</strong></span>\r\n          </div></td>\r\n        <td class="myriad15_gray"><span style="COLOR: #000000">&nbsp;</span></td>\r\n      </tr>\r\n      <tr>\r\n        <td colspan="2" class="arial12"><span style="COLOR: #000000">Fine dining Indian cuisine without the price tag. Stylish, sophisticated, delicious - what more \r\n            <br />could you want? </span></td>\r\n      </tr>\r\n      <tr>\r\n        <td width="88%" class="arial12">\r\n          <div align="right"><span style="COLOR: #000000"><strong>- Laura Lowe / Cardiff Life</strong></span>\r\n          </div></td>\r\n      </tr>\r\n    </tbody>\r\n  </table></p>', '', 2, '', '', '', '', '', '', '', '', '', '', '', '', ''),
